import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const Result = () => {
  const { state } = useLocation();
  const navigate = useNavigate();
  if (!state) return null;

  const { attempt, questions } = state;

  return (
    <div className="max-w-4xl mx-auto p-8">
      <div className="bg-white p-8 rounded shadow-lg text-center mb-8">
        <h2 className="text-3xl font-bold mb-4">Quiz Result</h2>
        <div className="text-6xl font-bold text-blue-600 mb-2">
          {((attempt.score / attempt.totalQuestions) * 100).toFixed(1)}%
        </div>
        <p className="text-xl text-gray-600">You got {attempt.score} out of {attempt.totalQuestions} correct!</p>
        <button 
          onClick={() => navigate('/dashboard')}
          className="mt-6 bg-blue-600 text-white px-8 py-2 rounded hover:bg-blue-700"
        >
          Back to Dashboard
        </button>
      </div>

      <h3 className="text-2xl font-bold mb-6">Review Answers</h3>
      <div className="space-y-6">
        {questions.map((q, idx) => {
          const resp = attempt.responses.find(r => r.questionId === q._id);
          const isCorrect = resp?.isCorrect;
          
          return (
            <div key={idx} className={`p-6 rounded shadow bg-white border-l-8 ${isCorrect ? 'border-green-500' : 'border-red-500'}`}>
              <p className="font-bold mb-4">{idx + 1}. {q.question}</p>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                {q.options.map((opt, i) => (
                  <div key={i} className={`p-3 rounded border ${
                    i === q.correct ? 'bg-green-50 border-green-200' : 
                    (i === resp?.selectedOption && !isCorrect ? 'bg-red-50 border-red-200' : 'bg-gray-50')
                  }`}>
                    {String.fromCharCode(65 + i)}. {opt}
                    {i === q.correct && <span className="ml-2 text-green-600 font-bold">✓ Correct Answer</span>}
                    {i === resp?.selectedOption && !isCorrect && <span className="ml-2 text-red-600 font-bold">✗ Your Answer</span>}
                  </div>
                ))}
              </div>
              <div className="bg-blue-50 p-4 rounded text-sm">
                <p className="font-bold text-blue-800 mb-1">Explanation:</p>
                <p className="text-blue-900">{q.explanation}</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Result;
