import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../api/axios';
import { Loader2 } from 'lucide-react';

const Quiz = () => {
  const { exam } = useParams();
  const [questions, setQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [responses, setResponses] = useState({});
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const { data } = await api.get(`/quiz/generate/${exam}`);
        setQuestions(data);
        setLoading(false);
      } catch (err) {
        console.error(err);
      }
    };
    fetchQuestions();
  }, [exam]);

  const handleOptionSelect = (optionIndex) => {
    setResponses({ ...responses, [currentIndex]: optionIndex });
  };

  const handleSubmit = async () => {
    const formattedResponses = Object.keys(responses).map(index => ({
      questionId: questions[index]._id,
      selectedOption: responses[index]
    }));

    try {
      const { data } = await api.post('/quiz/submit', { exam, responses: formattedResponses });
      navigate('/result', { state: { attempt: data, questions } });
    } catch (err) {
      console.error(err);
    }
  };

  if (loading) return <div className="flex h-screen items-center justify-center"><Loader2 className="animate-spin" /></div>;

  const currentQ = questions[currentIndex];

  return (
    <div className="max-w-4xl mx-auto p-8">
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold">{exam} Practice Quiz</h2>
        <span className="text-gray-600">Question {currentIndex + 1} of {questions.length}</span>
      </div>

      <div className="bg-white p-8 rounded shadow-lg mb-6">
        <p className="text-xl font-medium mb-6">{currentQ.question}</p>
        <div className="space-y-4">
          {currentQ.options.map((option, idx) => (
            <button
              key={idx}
              onClick={() => handleOptionSelect(idx)}
              className={`w-full text-left p-4 rounded border transition ${
                responses[currentIndex] === idx ? 'bg-blue-100 border-blue-500' : 'hover:bg-gray-50'
              }`}
            >
              {String.fromCharCode(65 + idx)}. {option}
            </button>
          ))}
        </div>
      </div>

      <div className="flex justify-between">
        <button
          disabled={currentIndex === 0}
          onClick={() => setCurrentIndex(currentIndex - 1)}
          className="bg-gray-300 px-6 py-2 rounded disabled:opacity-50"
        >
          Previous
        </button>
        {currentIndex === questions.length - 1 ? (
          <button onClick={handleSubmit} className="bg-green-600 text-white px-6 py-2 rounded">Submit Quiz</button>
        ) : (
          <button onClick={() => setCurrentIndex(currentIndex + 1)} className="bg-blue-600 text-white px-6 py-2 rounded">Next</button>
        )}
      </div>
    </div>
  );
};

export default Quiz;
